// --- MainCategoryCard Component ---
const MainCategoryCard = ({ category, onClick }) => {
  const displayedSubcategories = category.subcategories.slice(0, 4);
  const remainingSubcategoriesCount = category.subcategories.length - displayedSubcategories.length;

  return (
    <div
      className="relative flex flex-col justify-end w-full h-48 sm:h-56 rounded-2xl shadow-lg cursor-pointer
                 transition-all duration-300 ease-in-out transform overflow-hidden group
                 hover:scale-[1.03] hover:shadow-xl active:scale-98 active:shadow-md"
      onClick={() => onClick(category.id)}
    >
      {/* Subtle Background Image (from mainImage) - visible behind the "glass" */}
      <img
        src={category.mainImage}
        alt={category.name}
        className="absolute inset-0 w-full h-full object-cover" // Removed opacity to make it more noticeable behind the blur
        onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/250x250/E0E0E0/555555?text=BG`; }}
      />

      {/* Subcategory Image Collage - This is the "folder" with liquid glass effect */}
      <div
        className="absolute inset-0 grid grid-cols-2 grid-rows-2 gap-2 p-3 rounded-2xl
                   bg-white/20 backdrop-blur-md border border-white/30 shadow-inner" // Liquid glass effect (bg-white/20, backdrop-blur-md, border, shadow-inner)
      >
        {displayedSubcategories.map((sub, index) => (
          <div
            key={sub.id || index}
            className="w-full h-full flex items-center justify-center rounded-lg overflow-hidden
                       bg-white/40 border border-white/50 shadow-sm" // Individual item's "glass" container
          >
            <img
              src={sub.image}
              alt={sub.name}
              className="w-12 h-12 sm:w-14 sm:h-14 object-contain rounded-md transform transition-transform duration-200 group-hover:scale-110" // Smaller, centered image
              onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/80x80/A0A0A0/FFFFFF?text=${sub.name.charAt(0)}`; }}
            />
          </div>
        ))}
        {/* Add placeholders if there are fewer than 4 subcategories to maintain grid structure */}
        {Array.from({ length: 4 - displayedSubcategories.length }).map((_, i) => (
          <div key={`placeholder-${i}`} className="w-full h-full bg-white/30 rounded-lg flex items-center justify-center text-gray-700 text-xs">
            {/* You can put a generic icon or text here if desired */}
          </div>
        ))}
      </div>

      {/* Content Overlay for Name and Count - now at the bottom */}
      <div className="absolute inset-x-0 bottom-0 z-10 flex flex-col p-3 bg-gray-900/70 backdrop-blur-sm group-hover:bg-gray-900/85 transition-colors duration-300">
        <h3 className="text-white text-sm sm:text-base font-bold text-center leading-tight text-shadow-lg">
          {category.name}
        </h3>
        {remainingSubcategoriesCount > 0 && (
          <p className="text-gray-300 text-xs mt-1 font-medium opacity-80 group-hover:opacity-100 transition-opacity duration-200 text-center">
            + {remainingSubcategoriesCount} more
          </p>
        )}
      </div>
    </div>
  );
};

// --- SubCategoryModal Component (Updated for iOS-like UI) ---
const SubCategoryModal = ({ mainCategoryName, subcategories, onClose, onSelectSubCategory }) => {
  const [visibleItems, setVisibleItems] = useState([]);
  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    setVisibleItems([]); // Reset on new subcategories
    subcategories.forEach((sub, index) => {
      setTimeout(() => {
        setVisibleItems((prev) => [...prev, sub.id]);
      }, 70 * index); // Staggered animation for a smooth entrance
    });
  }, [subcategories]);

  const handleClose = () => {
    setIsClosing(true);
    setTimeout(onClose, 300); // Match with modal-fade-out duration
  };

  return (
    <div
      className={`fixed inset-0 z-50 flex items-center justify-center p-4 bg-black bg-opacity-60
                  ${isClosing ? 'animate-modal-overlay-fade-out' : 'animate-modal-overlay-fade-in'}`}
      onClick={handleClose} // Close modal when clicking outside
    >
      <div
        className={`bg-white/70 backdrop-blur-xl rounded-[2rem] shadow-2xl p-6 relative w-11/12 max-w-lg sm:max-w-xl md:max-w-2xl lg:max-w-4xl
                    max-h-[90vh] overflow-y-auto transform border border-white/50
                    ${isClosing ? 'animate-modal-zoom-out' : 'animate-modal-zoom-in'}`}
        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside modal
      >
        <button
          onClick={handleClose}
          className="absolute top-4 right-4 text-gray-700 bg-white/50 backdrop-blur-sm transition-colors text-xl p-2 rounded-full shadow-sm border border-white/50
                     hover:bg-white/70 active:scale-95"
          aria-label="Close modal"
        >
          <FaTimes />
        </button>

        <h2 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-6 text-center">
          {mainCategoryName} <span className="text-blue-600">Categories</span>
        </h2>

        {/* Subcategory Grid */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4 sm:gap-5 md:gap-6">
          {subcategories.map((sub) => (
            <div
              key={sub.id}
              className={`
                flex flex-col items-center p-3 sm:p-4 rounded-2xl shadow-sm cursor-pointer
                bg-white/40 backdrop-blur-sm border border-white/50 // Liquid glass effect for cards
                transition-all duration-300 ease-in-out transform
                hover:scale-[1.02] hover:shadow-md active:scale-98 active:shadow-lg
                ${visibleItems.includes(sub.id) ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}
              `}
              onClick={() => onSelectSubCategory(sub)}
            >
              {/* Image Container with Padding and object-contain */}
              <div className="w-full h-28 sm:h-32 flex items-center justify-center overflow-hidden rounded-xl mb-2 bg-blue-100/40 p-2 border border-blue-200/60"> {/* Translucent image background */}
                <img
                  src={sub.image}
                  alt={sub.name}
                  className="w-full h-full object-contain rounded-lg transform transition-transform duration-300 group-hover:scale-105"
                  onError={(e) => { e.target.onerror = null; e.target.src = `https://placehold.co/120x120/E0E0E0/333333?text=${encodeURIComponent(sub.name.split(' ')[0])}`; }}
                />
              </div>
              <p className="text-center text-sm font-semibold text-gray-800 leading-tight"> {/* Slightly bolder text */}
                {sub.name}
              </p>
              {sub.items && sub.items.length > 0 && (
                <span className="text-blue-600 text-xs mt-1">({sub.items.length} items)</span>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

// --- Main ProductCategory Component ---
const ProductCategory = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedMainCategory, setSelectedMainCategory] = useState(null);

  const handleMainCategoryClick = (categoryId) => {
    const category = productCategories.find(cat => cat.id === categoryId);
    if (category) {
      setSelectedMainCategory(category);
      setIsModalOpen(true);
    }
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedMainCategory(null);
  };

  const handleSelectSubCategory = (subCategory) => {
    console.log('Subcategory selected:', subCategory.name);
    handleCloseModal();
  };

  // React-slick settings for the carousel
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 4, // Default for larger screens
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: true,
    responsive: [
      {
        breakpoint: 1280, // xl
        settings: {
          slidesToShow: 4,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 1024, // lg
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 768, // md
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          centerMode: true, // Center the active slide
          centerPadding: '20px', // Padding on sides for partial next/prev slide
        },
      },
      {
        breakpoint: 640, // sm (mobile)
        settings: {
          slidesToShow: 1.5, // Show 1.5 cards for a better mobile carousel feel
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: '20px',
        },
      },
      {
        breakpoint: 480, // xs (extra small mobile)
        settings: {
          slidesToShow: 1.2, // Even smaller peek for very narrow screens
          slidesToScroll: 1,
          centerMode: true,
          centerPadding: '15px',
        },
      },
    ],
  };

  return (
    <div className="min-h-fit bg-gray-50 font-sans text-gray-800 p-4 sm:p-6 md:p-8 relative">
      <h1 className="text-2xl sm:text-3xl font-bold mb-8 text-center text-gray-900">Explore Our Categories</h1>

      {/* Main Category Carousel */}
      <div className="carousel-container w-full px-4 sm:px-0">
        <Slider {...sliderSettings}>
          {productCategories.map((category) => (
            <div key={category.id} className="p-2"> {/* Padding inside slide for spacing */}
              <MainCategoryCard
                category={category}
                onClick={handleMainCategoryClick}
              />
            </div>
          ))}
        </Slider>
      </div>

      {/* Subcategory Selection Modal */}
      {isModalOpen && selectedMainCategory && (
        <SubCategoryModal
          mainCategoryName={selectedMainCategory.name}
          subcategories={selectedMainCategory.subcategories}
          onClose={handleCloseModal}
          onSelectSubCategory={handleSelectSubCategory}
        />
      )}
    </div>
  );
};

export default ProductCategory;